/**
 * Invokes the initialization code passed by the in-process plugin object and
 * calls the appropriate callback depending on whether it was successful.
 * @param {Object} object The embed element representing the plugin on the
 *     HTML page.  This gets transmogrified during this function into a
 *     GEPlugin instance.
 * @param {function(GEPlugin)} successCB The callback that will be invoked if
 *     the plugin was created successfully.  The argument will be the plugin
 *     instance.
 * @param {function()} failureCB The callback that will be invoked if the
 *     plugin was not created successfully.
 * @private
 */
function setEarthInstance_(object, successCB, failureCB) {
  var ge = /** @type {GEPlugin} */ (object);
  if (!ge || !ge.getJavascriptInitCode_) {
    if (ge) {
      console.error('Failed to imbue the plugin embed with plugin-ness!');
    } else {
      console.error('Critical embed existence failure!');
    }
    failureCB();
    return;
  }
  var initCode = ge.getJavascriptInitCode_();
  var pluginObject = {};
  pluginObject['iface'] = ge;
  if (initCode) {
    var successCBWrapper = function(object) {
      successCB(ge);
    };
    var failureCBWrapper = function() {
      failureCB();
    };
    var initFunc = /** @type {Function} */ (eval(initCode));
    initFunc(pluginObject, successCBWrapper, failureCBWrapper);
  } else {
    console.error('Plugin returned null initCode.');
    failureCB();
  }
}

