goog.require('earth.Earth');
goog.require('earth.ValueCache');
goog.require('earth.ui.KmlTreeNodeRenderer');
goog.require('earth.ui.SelectionManager');
goog.require('goog.dom.xml');
goog.require('goog.ui.Control');
goog.require('goog.ui.registry');

goog.provide('earth.ui.KmlTreeNode');

/**
 * A goog.ui.Control subclass that displays a node in the KML tree.  This may
 * represent either a leaf node or a container; in the latter case, it will
 * automatically create and populate its child nodes.
 * @param {KmlFeature} model The KmlFeature represented by this node.
 * @param {earth.ui.SelectionManager} selectionManager This node's
 *     SelectionManager instance.  This will be propagated to any children.
 * @param {earth.Earth} earthInstance The Earth plugin instance.
 * @param {earth.ui.KmlTreeNodeRenderer=} opt_renderer See goog.ui.Control.
 * @param {goog.dom.DomHelper=} opt_domHelper See goog.ui.Control.
 * @constructor
 * @extends goog.ui.Control
 */
earth.ui.KmlTreeNode = function(model, selectionManager, earthInstance,
                                opt_renderer, opt_domHelper) {
  goog.base(this, null, opt_renderer, opt_domHelper);

  /**
   * Selection manager instance for tree.
   * @type {earth.ui.SelectionManager}
   * @private
   */
  this.selectionManager_ = selectionManager;

  /**
   * Earth instance for tree.
   * @type {earth.Earth}
   * @private
   */
  this.earth_ = earthInstance;

  /**
   * Cache used for lazy evaluation to avoid recomputing expensive properties.
   * @type {earth.ValueCache}
   * @private
   */
  this.cache_ = new earth.ValueCache();

  /**
   * Timer ID identifying the timer we use to determine whether a click event
   * is immediately followed by a double-click event.
   * @type {?number}
   * @private
   */
  this.clickTimer_ = null;

  /**
   * The DOM element for the name link.
   * @type {Element}
   * @private
   */
  this.nameLinkElement_ = this.getDomHelper().getElement(
      this.makeId(earth.ui.KmlTreeNodeRenderer.IdFragment.NAME_LINK));

  this.setSupportedState(goog.ui.Component.State.SELECTED, true);
  this.setSupportedState(goog.ui.Component.State.HOVER, true);
  // We support a much more robust selection model than goog.ui.Control does,
  // so we'll deal with this ourselves.
  this.setAutoStates(goog.ui.Component.State.SELECTED, false);
  this.setAutoStates(goog.ui.Component.State.ACTIVE, false);
  // The default mouse event handlers for goog.ui.Control don't let us handle
  // raw click events on subelements, which is a problem.  So we're going to
  // implement them ourselves instead.
  this.setHandleMouseEvents(false);
  this.setModel(model);
};
goog.inherits(earth.ui.KmlTreeNode, goog.ui.Control);

/**
 * Returns the model for this control, which is a KmlFeature.
 * @return {KmlFeature} The model for this control.
 * @override
 */
earth.ui.KmlTreeNode.prototype.getModel;

/**
 * The URL of the default icon that the plugin will give us for features with
 * no custom icon set.
 * @type {string}
 * @const
 */
earth.ui.KmlTreeNode.DEFAULT_ICON_URL =
    'http://maps.google.com/mapfiles/kml/pushpin/ylw-pushpin.png';

/** @inheritDoc */
earth.ui.KmlTreeNode.prototype.canDecorate = function(element) {
  return false;
};

/**
 * Returns the SelectionManager for this node.  The SelectionManager is used to
 * manage the selection of all the nodes in a given KML tree.
 * @return {earth.ui.SelectionManager} The SelectionManager.
 */
earth.ui.KmlTreeNode.prototype.getSelectionManager = function() {
  return this.selectionManager_;
};

/**
 * Returns the Earth instance for this node.
 * @return {earth.Earth} The Earth instance.
 */
earth.ui.KmlTreeNode.prototype.getEarth = function() {
  return this.earth_;
};

/**
 * Returns the name of this node's represented KmlFeature, which may include
 * HTML.  This may be considerably faster than calling getModel().getName()
 * because it is cached browser-side.
 * @return {string} The name of the feature.
 */
earth.ui.KmlTreeNode.prototype.getNameHtml = function() {
  return this.cache_.cachedValue(this.getNameHtml, function() {
    return this.getModel().getName();
  }, this);
};

/**
 * Returns the type of this node's represented KmlFeature.  This may be
 * considerably faster than calling getModel().getType() because it is cached
 * browser-side.
 * @return {string} The type of the feature.
 */
earth.ui.KmlTreeNode.prototype.getType = function() {
  return this.cache_.cachedValue(this.getType, function() {
    return this.getModel().getType();
  }, this);
};

/**
 * Returns the geometry of this node's represented KmlFeature, or null if it has
 * no geometry.  This may be considerably faster than calling
 * getModel().getGeometry() because it is cached browser-side.
 * @return {KmlGeometry} The geometry of the feature, or null.
 */
earth.ui.KmlTreeNode.prototype.getGeometry = function() {
  return this.cache_.cachedValue(this.getGeometry, function() {
    var model = this.getModel();
    var geometry = model.getGeometry && model.getGeometry();
    return geometry || null;
  }, this);
};

/**
 * Gets the effective snippet text for this feature.  This may or may not be
 * equal to the return value of getModel().getSnippet(); it will differ if the
 * feature has no <snippet> tag, but has a description that can be snippetized.
 * @return {string} The snippet text to use for this feature.  May be empty.
 */
earth.ui.KmlTreeNode.prototype.getSnippet = function() {
  return this.cache_.cachedValue(this.getSnippet, function() {
    if (this.getModel().getComputedSnippet) {
      return this.getModel().getComputedSnippet() || '';
    } else {
      return this.getModel().getSnippet() || '';
    }
  }, this);
};


/**
 * Gets the effective geometry type of this feature, or null if the feature
 * does not have any geometry.  This type may or may not be equal to the
 * return value of getGeometry().getType(); it will differ if the root
 * geometry is a MultiGeometry, but all subgeometries have the same type, in
 * which case the subgeometry type will be returned instead of
 * 'KmlMultiGeometry'.
 * @return {?string} The class name of the geometry type contained in this
 *     feature, or null if it has no geometry.
 */
earth.ui.KmlTreeNode.prototype.getGeometryType = function() {
  return this.cache_.cachedValue(this.getGeometryType, function() {
    if (!this.getModel().getGeometry) {
      return null;
    }
    var baseGeometry = this.getModel().getGeometry();
    if (!baseGeometry) {
      return null;
    }

    /**
     * Recursive helper function for getGeometryType.  Does the same thing as
     * getGeometryType, except will never return null because the geometry is
     * explicitly passed in.
     * @private
     * @param {!KmlGeometry} geometry The geometry to analyze.
     * @return {string} The class name of the geometry type contained within
     *     the given geometry.
     */
    function getGeometryTypeRecursive(geometry) {
      var geometryType = geometry.getType();
      if (geometryType == 'KmlMultiGeometry') {
        var geometries = geometry.getGeometries().getChildNodes();
        if (geometry.getGeometries().hasChildNodes()) {
          var firstType = getGeometryTypeRecursive(geometries.item(0));
          for (var i = geometries.getLength() - 1; i >= 1; --i) {
            var thisType = getGeometryTypeRecursive(geometries.item(i));
            if (thisType != firstType) {
              return geometry.getType();  // Which is 'KmlMultiGeometry'.
            }
          }
          return firstType;  // Which is the only type.
        }
        throw new Error('This function should return a value.');
      } else {
        return geometryType;
      }
    }
    return getGeometryTypeRecursive(baseGeometry);
  }, this);
};


/**
 * Returns the custom icon URL for this feature, which is calculated based on
 * the feature's ListStyle itemType as well as it's IconStyle.  This method is
 * rather expensive the first time it is called on a given node.
 * @return {?string} The custom icon URL for this feature, or null if there
 *     isn't one.
 */
earth.ui.KmlTreeNode.prototype.getCustomIconUrl = function() {
  return this.cache_.cachedValue(this.getCustomIconUrl, function() {
    /**
     * Returns the URL for the ItemIcon of the given feature's computed
     * ListStyle. This is the equivalent of calling
     * getComputedStyle().getListStyle().getIcon().getHref() on the feature.
     * @private
     * @param {KmlFeature} feature The feature whose ItemIcon URL we want.
     * @return {?string} The custom icon contained in this feature's ListStyle,
     *     or null if there isn't one.
     */
    var getListStyleIconUrl = goog.bind(function(feature) {
      var listStyle = feature.getComputedStyle().getListStyle();
      if (listStyle.getIcon) {
        var icon = listStyle.getIcon(this.earth_.plugin.ICON_STATE_DEFAULT);
        if (icon) {
          return icon.getHref();
        }
      }
      return null;
    }, this);

    /**
     * Returns the URL for the Icon of the given feature's computed IconStyle.
     * This is the equivalent of calling
     * getComputedStyle().getIconStyle().getIcon().getHref() on the feature.
     * @private
     * @param {KmlFeature} feature The feature whose ItemIcon URL we want.
     * @return {?string} The custom icon contained in this feature's ListStyle,
     *     or null if there isn't one.
     */
    var getIconStyleIconUrl = goog.bind(function(feature) {
      var icon = feature.getComputedStyle().getIconStyle().getIcon().getHref();
      if (icon == earth.ui.KmlTreeNode.DEFAULT_ICON_URL) {
        return null;
      }
      return icon;
    }, this);

    var listItemIconUrl = getListStyleIconUrl(this.getModel());
    if (listItemIconUrl) {
      return listItemIconUrl;
    }
    // This may still return null.
    return getIconStyleIconUrl(this.getModel());
  }, this);
};


/**
 * Returns the CSS class name that should be used to display the default icon
 * for this feature.  In practice, this will be the feature's geometry type if
 * it has geometry, and the feature's type otherwise.
 * @return {string} The CSS class name that should be used to display the
 *    default icon for this feature.
 */
earth.ui.KmlTreeNode.prototype.getDefaultIconClass = function() {
  return this.cache_.cachedValue(this.getDefaultIconClass, function() {
    if (this.getModel().getGeometry) {
      var geometryType = this.getGeometryType();
      if (geometryType !== null) {
        return geometryType;
      }
    }
    return this.getModel().getType();
  }, this);
};


/**
 * Returns whether the feature for this model can contain children.  This will
 * return true even if the container is empty.
 * TODO(phoenix): This will eventually depend on the presentation of the node.
 * @return {boolean} True if the feature can contain children.
 */
earth.ui.KmlTreeNode.prototype.isContainer = function() {
  return this.cache_.cachedValue(this.isContainer, function() {
    // Special case for network links.  We don't support these yet because
    // the plugin lacks the necessary event handlers, so we want to treat them
    // like leaf nodes for the time being.
    if (this.getModel().getType() == 'KmlNetworkLink') {
      return false;
    }
    return goog.isDefAndNotNull(this.getModel().getFeatures);
  }, this);
};

/**
 * Returns whether the feature should display any children. Returns false
 * if the feature is not a container or its list item type is "hide children".
 * @return {boolean} True if the feature should display any children.
 */
earth.ui.KmlTreeNode.prototype.showsChildren = function() {
  return this.cache_.cachedValue(this.showsChildren, function() {
      if (this.isContainer()) {
        var listStyle = this.getModel().getComputedStyle().getListStyle();
        var listItemType = listStyle.getListItemType();
        return listItemType != this.earth_.plugin.LIST_ITEM_CHECK_HIDE_CHILDREN;
      } else {
        return false;
      }
  }, this);
};

/**
 * Handles single click events on any of this node's DOM elements.
 * Use event.target to figure out which one was clicked on.
 * This function is NOT an override, and has nothing to do with
 * goog.ui.Control.handleMouseUp, goog.ui.Control.handleDblClick, etc.
 * @param {goog.events.Event} event The click event.
 */
earth.ui.KmlTreeNode.prototype.handleClickEvent = function(event) {
  event.stopPropagation();

  // We don't want to do this on a double click, only a single click, so we'll
  // set a zero-length timer and cancel it if we get a double-click event before
  // the next event loop cycle. We still need to keep track of the event and
  // target, but goog.Timer.callOnce doesn't allow us to pass its function any
  // arguments.  So we'll store the event and target in a helper object which
  // can be invoked by callOnce and then pass them on to the real event handler.
  // Poor man's currying for the win? (For the record, we have to store the
  // event target separately or it will be null by the time it gets to our
  // handler.)
  var doHandleClickEvent_ = this.doHandleClickEvent_;
  var node = this;
  var helper = {
    event_: event,
    eventTarget_: event.target,
    handleEvent: function() {
      var realHandler = goog.bind(doHandleClickEvent_, node);
      realHandler(this.event_, this.eventTarget_);
    }
  };

  if (this.clickTimer_) {
    goog.Timer.clear(this.clickTimer_);
  }
  this.clickTimer_ = goog.Timer.callOnce(helper, 0, this);
};

/**
 * Contains the actual handler for click events, which will be invoked on a
 * timer from handleClickEvent if a double-click is not registered.
 * @param {goog.events.Event} event The click event.
 * @param {Element} target The DOM element that was actually clicked on.
 * @private
 */
earth.ui.KmlTreeNode.prototype.doHandleClickEvent_ = function(event, target) {
  if (target == this.nameLinkElement_) {
    this.showFeatureBalloon();
  }
  if (event.platformModifierKey) {
    this.setSelected(!this.isSelected());
  } else {
    this.selectionManager_.deselectAll();
    this.setSelected(true);
  }
};

/**
 * Handles double-click events on any of this node's DOM elements.
 * Use event.target to figure out which one was clicked on.
 * This function is NOT an override, and has nothing to do with
 * goog.ui.Control.handleMouseUp, goog.ui.Control.handleDblClick, etc.
 * @param {goog.events.Event} event The double-click event.
 */
earth.ui.KmlTreeNode.prototype.handleDoubleClickEvent = function(event) {
  event.stopPropagation();

  if (this.clickTimer_) {
    goog.Timer.clear(this.clickTimer_);
    this.clickTimer_ = null;
  }

  this.doHandleDoubleClickEvent_(event, event.target);
};

/**
 * Contains the actual handler for double-click events, which will be invoked
 * from handleDoubleClickEvent.  This is a separate method mainly for symmetry
 * with doHandleClickEvent_.
 * @param {goog.events.Event} event The click event.
 * @param {Object|undefined} target The DOM element that was actually clicked
 *     on.
 * @private
 */
earth.ui.KmlTreeNode.prototype.doHandleDoubleClickEvent_ = function(event,
                                                                    target) {
  // Currently we do this no matter what was clicked.
  this.flyToFeature();
};

/**
 * Handles drag-start events on any of this node's DOM elements.  Since only
 * the node's root element has dragging enabled, this is unlikely to be any
 * other element.
 * @param {goog.events.Event} event The drag-start event.
 */
earth.ui.KmlTreeNode.prototype.handleDragStartEvent = function(event) {
  event.stopPropagation();

  this.setSelected(true);

  /**
   * @type {Array.<KmlObject>}
   */
  var selectedFeatures = this.selectionManager_.getSelectedObjects();
  var dragFeature;
  if (selectedFeatures.length == 1) {
    dragFeature = selectedFeatures[0];
  } else {
    /**
     * @desc The title of a newly created document containing features that
     *       have been dragged from the search panel.
     * @type {string}
     * @const
     */
    var MSG_DOCUMENT_NAME = goog.getMsg('Dragged Items');

    // Wrap the features in a new unnamed document and copy that.
    var newDocument = this.earth_.plugin.createDocument('');
    newDocument.setName(MSG_DOCUMENT_NAME);
    goog.array.forEach(selectedFeatures, function(element, index, array) {
      newDocument.getFeatures().appendChild(element);
    });
    dragFeature = newDocument;
  }

  var evt = event.getBrowserEvent();
  evt.effectAllowed = 'copy';
  // Sadly, we can't use 'application/vnd.google-earth.kml+xml' as the
  // data format, because WebKit can't convert that to a pasteboard type
  // to export the drag to the OS.  'text/xml' doesn't work either.
  evt.dataTransfer.setData('text/plain', dragFeature.getKml());
};

/**
 * Shows the balloon for this node's feature.
 */
earth.ui.KmlTreeNode.prototype.showFeatureBalloon = function() {
  var kmlFeature = this.getModel();
  var balloon = this.earth_.plugin.createFeatureBalloon('');
  balloon.setFeature(kmlFeature);
  this.earth_.plugin.setBalloon(balloon);
};


/**
 * Flies the camera to the view of this node's feature.
 */
earth.ui.KmlTreeNode.prototype.flyToFeature = function() {
  this.earth_.flyToBoundingView(this.getModel());
};


/**
 * Copies the KML of this node's feature to the system clipboard.
 */
earth.ui.KmlTreeNode.prototype.copyAsKml = function() {
  // Copying text to the clipboard is pretty much impossible except in response
  // to a system copy event (Edit > Copy or Ctrl/Command+C), which would require
  // us to have some kind of persistent selection in the left panel so we know
  // what to copy.  In lieu of that, we send the clipboard contents to Earth to
  // be copied.
  if (this.getModel().copyToClipboard_) {
    this.getModel().copyToClipboard_();
  }
};

/**
 * Saves this node's feature to My Places.
 */
earth.ui.KmlTreeNode.prototype.saveToMyPlaces = function() {
  if (this.getModel().saveToMyPlaces_) {
    this.getModel().saveToMyPlaces_();
  }
};

/**
 * Calls setSelected() on this node and all of its descendants, passing the
 * given value.  To select or deselect just this node, call setSelected()
 * (which is inherited from goog.ui.Control).
 * @param {boolean} selected Whether this node and its descendants should be
 *     selected.
 */
earth.ui.KmlTreeNode.prototype.setSelectedRecursive = function(selected) {
  this.setSelected(selected);
  this.forEachChild(function(child, index) {
    if (child.setSelectedRecursive) {
      child.setSelectedRecursive(selected);
    }
  });
};

/**
 * @return {goog.ui.Button} Visibility button control.
 */
earth.ui.KmlTreeNode.prototype.getVisibilityButton = function() {
  return /** @type {goog.ui.Button} */ (this.getChild(this.makeId(
      earth.ui.KmlTreeNodeRenderer.IdFragment.VISIBILITY_BUTTON)));
};

/**
 * Returns a KML feature's inherited visibility, which is only true if
 * its visibility and all its ancestors' visibilities are true.
 *
 * @return {boolean} KML inherited visibility.
 */
earth.ui.KmlTreeNode.prototype.getKmlInheritedVisibility = function() {
  if (this.getModel().getVisibility()) {
    var parent = this.getParent();
    if (parent && parent.getKmlInheritedVisibility) {
      return parent.getKmlInheritedVisibility();
    } else {
      return true;
    }
  } else {
    return false;
  }
};

goog.ui.registry.setDefaultRenderer(earth.ui.KmlTreeNode,
                                    earth.ui.KmlTreeNodeRenderer);

