/**
 * @fileoverview A KmlTree class, which is used to render the HTML/JS tree view
 * in the Google Earth client.
 */

goog.require('earth.Earth');
goog.require('earth.ui.KmlTreeNode');
goog.require('earth.ui.KmlTreeNodeRenderer');
goog.require('earth.ui.SelectionManager');
goog.require('goog.ui.Component');

goog.provide('earth.ui.KmlTree');

/**
 * A goog.ui.Component subclass that displays a KML feature hierarchy in a tree.
 * This class provides an API to load the tree, and also manages its selection
 * by implementing the SelectionManager interface.  It delegates most of the
 * other work to KmlTreeNode, which you should not need to interact with
 * directly in most cases.
 * @param {earth.Earth} earthInstance The Earth instance this tree will use to
 *     parse and otherwise deal with KML.
 * @param {goog.dom.DomHelper=} opt_domHelper If passed, this DomHelper will
 *     be used for all DOM operations.
 * @constructor
 * @extends goog.ui.Component
 * @implements {earth.ui.SelectionManager}
 */
earth.ui.KmlTree = function(earthInstance, opt_domHelper) {
  goog.base(this, opt_domHelper);
  this.earth_ = earthInstance;
};
goog.inherits(earth.ui.KmlTree, goog.ui.Component);

/**
 * Loads (or reloads) this KmlTree from the given KML string.
 * @param {string} kmlString The KML string to parse.
 */
earth.ui.KmlTree.prototype.loadFromKmlString = function(kmlString) {
  var kmlRoot = this.earth_.plugin.parseKml(kmlString);
  this.loadFromKmlFeature(/** @type {KmlFeature} */ (kmlRoot));
};

/**
 * Loads (or reloads) this KmlTree from the given root KML feature.
 * If |kmlFeature| is not a container, this will be a very boring tree.
 * @param {KmlFeature} kmlFeature The feature to load.
 */
earth.ui.KmlTree.prototype.loadFromKmlFeature = function(kmlFeature) {
  this.setModel(kmlFeature);
  var rootNode = new earth.ui.KmlTreeNode(kmlFeature, this, this.earth_,
                                               undefined, this.getDomHelper());
  this.loadFromKmlTreeNode(rootNode);
};

/**
 * Loads (or reloads) this KmlTree from the given KmlTreeNode.
 * @param {earth.ui.KmlTreeNode} node The node to load.
 * @protected
 */
earth.ui.KmlTree.prototype.loadFromKmlTreeNode = function(node) {
  this.removeChildren(true);
  this.addChild(node, true);
};

/**
 * Replaces this tree's Earth plugin instance with the passed one.  Since this
 * makes all our feature references invalid, calling this has the side effect of
 * removing all the nodes in the tree (unless the passed instance was the one
 * we were already using, in which case nothing happens).
 * @param {earth.Earth} earthInstance The new Earth instance to use.
 */
earth.ui.KmlTree.prototype.setEarthInstance = function(earthInstance) {
  if (this.earth_ != earthInstance) {
    this.removeChildren(true);
    this.earth_ = earthInstance;
  }
};

/**
 * @inheritDoc
 */
earth.ui.KmlTree.prototype.deselectAll = function() {
  this.forEachChild(function(child, index) {
    child.setSelectedRecursive(false);
  });
};

/**
 * Returns a list of all currently selected features.  In the case where both a
 * parent feature and some or all of its child features are selected, the child
 * features will be omitted from the list.  This has the unfortunate effect that
 * it is not possible to select a subset of a container's children and also
 * select the container itself, but to do otherwise would turn this list into a
 * tree and vastly complicate the selection logic.
 * @return {Array.<KmlFeature>} The list of all features that are currently
 *     selected.
 * @override
 */
earth.ui.KmlTree.prototype.getSelectedObjects = function() {
  var selectedObjects = [];
  function getSelectedObjectsRecurse(node, objects) {
    if (node.isSelected()) {
      objects.push(node.getModel());
    } else {
      node.forEachChild(function(child, index) {
        getSelectedObjectsRecurse(child, objects);
      });
    }
  }
  this.forEachChild(function(child, index) {
    getSelectedObjectsRecurse(child, selectedObjects);
  });
  return selectedObjects;
};

