// Copyright 2011 Google Inc. All Rights Reserved.

/**
 * @fileoverview Initialization code for the KML renderer.  This creates the
 * in-process plugin instance, connects to the Earth bridge to await the KML
 * to load into the view, and then instantiates a KmlTree to display the KML
 * when it arrives.
 * This file is not necessary to use KmlTree as a library class.
 */

goog.require('earth.Earth');
goog.require('earth.ui.KmlTree');
goog.require('goog.dom');

/**
 * The Earth instance we will be using to communicate with the Earth client.
 * @type {earth.Earth}
 * @private
 */
var earth_ = null;

/**
 * Init callback - invoked if plugin instantiation succeeded.
 * @param {earth.Earth} earthInstance The Earth instance that was successfully
 * created.
 */
function initCb(earthInstance) {
  var kmlTree = null;

  var updateKml = function() {
    var kmlDocument = /** @type {KmlDocument} */
        (earth_.plugin.getGlobe().getLeftPanelKml_());
    if (kmlDocument) {
      earth_.extensions.dom.clearFeatures();
      earth_.plugin.getFeatures().appendChild(kmlDocument);
      if (kmlTree) {
        kmlTree.dispose();
      }
      kmlTree = new earth.ui.KmlTree(earth_);
      kmlTree.decorate(goog.dom.getElement('left_panel'));
      kmlTree.loadFromKmlFeature(kmlDocument);
      earth_.flyToBoundingView(kmlDocument);
    }
  };

  // Listen to the Earth bridge for KML changes.
  google.earth.addEventListener(earthInstance.plugin, 'kmlchange_', updateKml);

  // And invoke manually once now in case we already got KML.
  updateKml();
}

// Failure callback - invoked if plugin instantiation failed.
function failureCb() {
  // TODO(phoenix): Probably should think of some better way to handle this,
  // but this should never happen since the Earth plugin is in-process.
  console.log('failed to create in-process plugin!');
}

window.addEventListener('load', function() {
  earth_ = new earth.Earth();
  earth_.addListener(this, initCb, failureCb);
  earth_.load();
}, false);
